document.getElementById('loginForm').addEventListener('submit',function(event){event.preventDefault();const username=document.getElementById('username').value;const password=document.getElementById('password').value;console.log(username);console.log(password);fetch('http://catcafe.localhost:8000/api/method/login',{method:'POST',headers:{'Accept':'application/json','Content-Type':'application/json',},body:JSON.stringify({usr:username,pwd:password})}).then(r=>{if(!r.ok){throw new Error('Error en la solicitud: '+r.status);}
return r.json();}).then(r=>{console.log(r);}).catch(error=>{console.error('Error:',error);});});